mapper.update:
UPDATE user
SET user_name = #{userName},
    password  = #{password}
WHERE user_id = #{userId}

UPDATE user
SET show_status = 1
WHERE user_id = #{userId}

mapper.select:
SELECT count(*)
FROM user
WHERE user_name = #{userName}
  AND password = #{password}
  AND show_status = 0

SELECT user_id
FROM user
WHERE user_name = #{userName}

select count(*)
from user
where user_name = #{userName}
